import { useReducer } from "react";

import CartContext from "./cart-context";

const defaultCartState = {
    items: [],
    totalAmount: 0
};

const cartReducer = (state, action) => {
    if (action.type === 'ADD') {
        const updatedTotalAmount = state.totalAmount + action.item.price * action.item.amount;

        const exsitingCartItemIdex = state.items.findIndex(
            item => item.id === action.item.id
        );

        const exsitingCartItem = state.items[exsitingCartItemIdex];
        let updatedItems;

        if (exsitingCartItem) {
            const updatedItem = {
                ...exsitingCartItem,
                amount: exsitingCartItem.amount + action.item.amount
            };
            updatedItems = [...state.items];
            updatedItems[exsitingCartItemIdex] = updatedItem;
        } else {
            updatedItems = state.items.concat(action.item);
        }

        return {
            items: updatedItems,
            totalAmount: updatedTotalAmount
        };
    }

    if (action.type === 'REMOVE') {
        const exsitingCartItemIdex = state.items.findIndex(
            item => item.id === action.id
        );

        const exsitingItem = state.items[exsitingCartItemIdex];
        const updatedTotalAmount = state.totalAmount - exsitingItem.price;

        let updatedItems;
        if(exsitingItem.amount === 1) {
            updatedItems = state.items.filter(item => item.id != action.id) 
        } else {
            const updatedItem = {...exsitingItem, amount: exsitingItem.amount - 1};
            updatedItems = [...state.items];
            updatedItems[exsitingCartItemIdex] = updatedItem;

        }

        return {
            items: updatedItems,
            amount: updatedTotalAmount
        }
    }

    return defaultCartState;
};

const CartProvider = props => {
    const [cartState, dispatchCartAction] = useReducer(cartReducer, defaultCartState);

    const addItemHandler = item => {
        dispatchCartAction({ type: 'ADD', item: item });
    };

    const removeItemHandler = id => {
        dispatchCartAction({ type: 'REMOVE', id: id });
    };

    const cartContext = {
        items: cartState.items,
        totalAmount: cartState.totalAmount,
        addItem: addItemHandler,
        removeItem: removeItemHandler
    };

    return (
        <CartContext.Provider value={cartContext}>
            {props.children}
        </CartContext.Provider>
    );
};

export default CartProvider;
